// src/redux/actions.js
export const EDIT_TODO = 'EDIT_TODO';

export const addTodo = (text) => ({
    type: 'ADD_TODO',
    payload: {
      id: new Date().getTime(),
      text,
    },
  });

  export const removeTodo = (id) => ({
    type: 'REMOVE_TODO',
    payload: id,
  });
