// src/redux/actions.js

export const editTodo = (id, newText) => ({
    type: 'EDIT_TODO',
    payload: { id, newText },

  });
export const addTodo = (text) => ({
    type: 'ADD_TODO',
    payload: {

      text,
    },
  });

  export const removeTodo = (id) => ({
    type: 'REMOVE_TODO',
    payload: id,
  });
