import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { removeTodo } from '../redux/actions';

const TodoItem = ({ todo }) => {
  const dispatch = useDispatch();
  const [completed, setCompleted] = useState(false);

  const handleCheckboxChange = () => {
    setCompleted(!completed);
  };

  const todoStyle = completed ? 'line-through text-gray-400' : 'text-gray-800';

  return (
    <div className="bg-white p-4 rounded-md shadow-md mb-4 flex justify-between items-center hover:shadow-lg">
      <div className="flex items-center">
        <input
          type="checkbox"
          className="mr-2"
          checked={completed}
          onChange={handleCheckboxChange}
        />
        <p className={`text-lg ${todoStyle}`}>{todo.text}</p>
      </div>
      <button
        onClick={() => dispatch(removeTodo(todo.id))}
        className="bg-red-500 text-white px-2 py-1 rounded-md hover:bg-red-600 transition duration-300 ease-in-out"
      >
        Delete
      </button>
    </div>
  );
};

export default TodoItem;
