import axios from 'axios'
export const editTodo = (id, newText) => ({
    type: 'EDIT_TODO',
    payload: { id, newText },

  });
export const addTodo = (text) => ({
    type: 'ADD_TODO',
    payload: {
      id: new Date().getTime(),
      text,
    },
  });

  export const removeTodo = (id) => ({
    type: 'REMOVE_TODO',
    payload: id,
  });

  export const addTodoFunction = (dispatch) => {
    dispatch({type:'LOGIN_REQUEST'});
  return axios.get("http://localhost:3004/todos").then((res) => {
         console.log(res.data);
        dispatch({type:'Ge_TODO', payload:res.data});
    }).catch((err) => {
        dispatch({type:'LOGIN_FAILURE'});
    });
};
