import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { removeTodo } from '../redux/actions';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash } from '@fortawesome/free-solid-svg-icons';
import { faPenToSquare } from '@fortawesome/free-solid-svg-icons';
import { editTodo } from '../redux/actions';
import { faCheck } from '@fortawesome/free-solid-svg-icons';
import { faTimes } from '@fortawesome/free-solid-svg-icons';




const TodoItem = ({ todo, id }) => {
  const dispatch = useDispatch();
  const [isEditing, setIsEditing] = useState(false);
  const [completed, setCompleted] = useState(false);
  const [editedText, setEditedText] = useState(todo.text); // Define the editedText state variable
  const handleCancelClick = () => {
    setIsEditing(false);
    setEditedText(todo.text); 
  };
  const handleSaveClick = () => {
    dispatch(editTodo(todo.id, editedText)); // Dispatch the editTodo action with the updated text
    setIsEditing(false);
  };
  const handleEditClick = () => {
    setIsEditing(true);
  };

  const handleTextChange = (e) => {
    setEditedText(e.target.value);
  };
  const handleCheckboxChange = () => {
    setCompleted(!completed);
  };
console.log(completed,"compltd")
  const todoStyle = completed ? { textDecoration: 'line-through' } : 'text-gray-800';

  return (
    <div className="bg-white p-4 rounded-md shadow-md mb-4 flex justify-between items-center hover:shadow-lg">
      <div className="flex items-center">
        <p className={`text-lg px-2`}>{id+1}.</p>
        <input
          type="checkbox"
          className="mr-2"
          checked={completed}
          onChange={handleCheckboxChange}
        />
  {isEditing ? (
          <div className="flex items-center " >
            <input
              type="text"
              className="mr-2 border-b border-gray-500"
              value={editedText}
              onChange={handleTextChange}
            />
            <button  className="text-green-600 " >

              <FontAwesomeIcon className='px-2' onClick={handleSaveClick} icon={faCheck} />
            </button>
            <button  className="text-red-600 ml-2">

              <FontAwesomeIcon  onClick={handleCancelClick} icon={faTimes} />
            </button>
          </div>
        )


        : completed ? 

         <p className={`text-lg lines`}>{todo.text}</p>
        :
         <p className={`text-lg `}>{todo.text}</p>
        }
      </div>
      {!isEditing && (
  <button

  className=" px-2 py-1 rounded-md hover:bg-red-600 transition duration-300 ease-in-out"
>
   <FontAwesomeIcon  className='px-2' icon={faTrash}   onClick={() => dispatch(removeTodo(todo.id))}/> 
   {!completed && !isEditing && (
   <FontAwesomeIcon className='px-2' icon={faPenToSquare}  onClick={handleEditClick} />  

   )}

</button>
      )}





  </div>
  );
};

export default TodoItem;
