import React from 'react';
import { useDispatch } from 'react-redux';
import { removeTodo } from '../redux/actions';

const TodoItem = ({ todo }) => {
  const dispatch = useDispatch();

  return (
    <div className="bg-white p-4 rounded-md shadow-md mb-4 flex justify-between items-center hover:shadow-lg">
      <p className="text-gray-800 text-lg">{todo.text}</p>
      <button
        onClick={() => dispatch(removeTodo(todo.id))}
        className="bg-red-500 text-white px-2 py-1 rounded-md hover:bg-red-600 transition duration-300 ease-in-out"
      >
        Delete
      </button>
    </div>
  );
};

export default TodoItem;
