import React, { useState } from 'react';
import { Input, Button, Text } from '@chakra-ui/react';
import { useDispatch, useSelector } from 'react-redux';
import { getUserData } from '../Redux/AuthReducer/action';
// import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const userData = useSelector(store => store.authReducer.userData);
  const dispatch = useDispatch();
//   const navigate = useNavigate();

  console.log(userData);

  const handleLogin = () => {
    // dispatch(getUserData);
    if (email === "admin@mail.com" && password === "admin") {
    //   navigate("/admin");
    } else if (email && password) {
      for (let i = 1; i < userData.length; i++) {
        if (userData[i].email === email && userData[i].password === password) {
          localStorage.setItem("userName", JSON.stringify(userData[i].name));
        //   navigate("/");
          break;
        }
      }
    } else {
      alert("Wrong Credentials");
    }
  }

  return (
    <div className="w-1/5 mx-auto mt-10">
      <Text fontSize="xl" color="gray.400">Email Address</Text>
      <Input
        type="email"
        placeholder="Enter the email"
        value={email}
        className="mb-4"
        onChange={(e) => setEmail(e.target.value)}
      />
      <Text fontSize="xl" color="gray.400">Password</Text>
      <Input
        type="password"
        placeholder="Enter password"
        value={password}
        className="mb-4"
        onChange={(e) => setPassword(e.target.value)}
      />
      <Button
        size="md"
        height="12"
        width="48"
        borderWidth="2"
        borderColor="green.500"
        className="mb-4"
        onClick={handleLogin}
      >
        Sign In
      </Button>
    </div>
  );
}

export default Login;
