import React, { useState } from 'react'

function Signup() {
    const [formData, setFormData] = useState({
        username: '',
        password: '',
      });

      const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
      };

      const handleSignup = () => {
        // Validate and process the signup data
        if (formData.username && formData.password) {
          // Store the user data in localStorage (for simplicity)
          localStorage.setItem(formData.username, formData.password);
          alert('Signup successful! Please login.');
          // Reset the form
          setFormData({ username: '', password: '' });
        } else {
          alert('Please fill in both username and password fields.');
        }
      };

    return (
        <div id="loginform">

            <h2 title="Login" id="headerTitle">Signup</h2>
            <div>
            <div class="row">
                    <label>Email</label>
                    <input type="email"   name="email"
          value={formData.email}
          onChange={handleInputChange} placeholder="Enter your Email" />
                </div>
                <div class="row">
                    <label>Username</label>
                    <input type="text"   name="username"
          value={formData.username}
          onChange={handleInputChange} placeholder="Enter your Username" />
                </div>
                <div class="row">
                    <label>Password</label>
                    <input type="password"   name="password"
          value={formData.pass}
          onChange={handleInputChange} placeholder="Enter your Password" />
                </div>
                <div id="button" class="row">
                    <button>Signup</button>
                </div>
            </div>
            <div id="alternativeLogin">
                <label>already have  account? <span style={{text:'blue'}}>login</span></label>
                <div id="iconGroup">

                </div>
            </div>
        </div>
    )
}

export default Signup
